import 'dart:collection';import 'package:flutter/material.dart';LinkedHashMap<OverlayBase, BuildContext> baseContext = LinkedHashMap();class OverlayBase extends StatelessWidget {  final Widget child;  TextDirection textDirection;  OverlayBase({this.child, this.textDirection}) {    if (textDirection == null) textDirection = TextDirection.ltr;  }  @override  Widget build(BuildContext context) {    return Directionality(      textDirection: textDirection,      child: Overlay(        initialEntries: [          OverlayEntry(            builder: (ctx) {              baseContext[this] = ctx;              return child;            },          ),        ],      ),    );  }}