import 'package:flutter/cupertino.dart';import 'package:flutter_localizations/flutter_localizations.dart';import 'package:flutter_waya/src/custom/OverlayBase.dart';import 'package:flutter_waya/src/utils/BaseNavigatorUtils.dart';class OverlayCupertino extends StatelessWidget {  TextDirection textDirection;  Iterable<Locale> supportedLocales;  Locale locale;  Iterable<LocalizationsDelegate<dynamic>> localizationsDelegates;  List<NavigatorObserver> navigatorObservers;  Map<String, WidgetBuilder> routes;  String title;  final GlobalKey<NavigatorState> navigatorKey;  final Widget home;  final CupertinoThemeData theme;  final String initialRoute;  final RouteFactory onGenerateRoute;  final RouteFactory onUnknownRoute;  final TransitionBuilder builder;  final GenerateAppTitle onGenerateTitle;  final Color color;  final LocaleListResolutionCallback localeListResolutionCallback;  final LocaleResolutionCallback localeResolutionCallback;  bool showPerformanceOverlay;  bool checkerboardRasterCacheImages;  bool checkerboardOffscreenLayers;  bool showSemanticsDebugger;  bool debugShowCheckedModeBanner;  OverlayCupertino({    Key key,    this.textDirection,    this.navigatorKey,    this.home,    this.theme,    this.routes,    this.initialRoute,    this.onGenerateRoute,    this.onUnknownRoute,    this.navigatorObservers,    this.builder,    this.title,    this.onGenerateTitle,    this.color,    this.locale,    this.localizationsDelegates,    this.localeListResolutionCallback,    this.localeResolutionCallback,    this.supportedLocales,    this.showPerformanceOverlay,    this.checkerboardRasterCacheImages,    this.checkerboardOffscreenLayers,    this.showSemanticsDebugger,    this.debugShowCheckedModeBanner,  }) :super(key: key) {    if (showPerformanceOverlay == null) showPerformanceOverlay = false;    if (checkerboardRasterCacheImages == null) checkerboardRasterCacheImages = false;    if (checkerboardOffscreenLayers == null) checkerboardOffscreenLayers = false;    if (showSemanticsDebugger == null) showSemanticsDebugger = false;    if (debugShowCheckedModeBanner == null) debugShowCheckedModeBanner = true;    if (title == null) title = "";    if (routes == null) routes = const <String, WidgetBuilder>{};    if (textDirection == null) textDirection = TextDirection.ltr;    if (navigatorObservers == null) navigatorObservers = [BaseNavigatorUtils.getInstance()];    if (locale == null) locale = const Locale('zh');    if (supportedLocales == null) supportedLocales = [const Locale('zh', 'CH')];    if (localizationsDelegates == null)      localizationsDelegates = [GlobalMaterialLocalizations.delegate, GlobalWidgetsLocalizations.delegate];  }  @override  Widget build(BuildContext context) {    return OverlayBase(        textDirection: textDirection,        child: CupertinoApp(          navigatorKey: navigatorKey,          home: home,          theme: theme,          routes: routes,          initialRoute: initialRoute,          onGenerateRoute: onGenerateRoute,          onUnknownRoute: onUnknownRoute,          navigatorObservers: navigatorObservers,          builder: builder,          title: title,          onGenerateTitle: onGenerateTitle,          color: color,          locale: locale,          localizationsDelegates: localizationsDelegates,          localeListResolutionCallback: localeListResolutionCallback,          localeResolutionCallback: localeResolutionCallback,          supportedLocales: supportedLocales,          showPerformanceOverlay: showPerformanceOverlay,          checkerboardRasterCacheImages: checkerboardRasterCacheImages,          checkerboardOffscreenLayers: checkerboardOffscreenLayers,          showSemanticsDebugger: showSemanticsDebugger,          debugShowCheckedModeBanner: debugShowCheckedModeBanner,        ));  }}