class DateTimePickerUnit {  final String year;  final String month;  final String day;  final String hour;  final String minute;  final String second;  DateTimePickerUnit({this.year, this.month, this.day, this.hour, this.minute, this.second});  int getLength() {    int i = 0;    if (year != null) i = i + 1;    if (month != null) i = i + 1;    if (day != null) i = i + 1;    if (hour != null) i = i + 1;    if (minute != null) i = i + 1;    if (second != null) i = i + 1;    return i;  }  DateTimePickerUnit getDefaultUnit() {    return DateTimePickerUnit(year: 'Y',        month: 'M',        day: 'D',        hour: 'H',        minute: 'M',        second: 'S');  }}