import 'package:flutter/material.dart';import 'package:flutter_waya/flutter_waya.dart';import 'package:flutter_waya/src/constant/WayColor.dart';import 'package:flutter_waya/src/custom/OverlayBase.dart';showToast(message) {  AlertUtils.showToast(message.toString());}showToastWidget(Widget widget) {  AlertUtils.showToastWidget(widget);}class AlertUtils {  static BuildContext context = baseContext.values.first;  static showToast(String message) {    AlertUtils.showToastWidget(Container(      decoration: BoxDecoration(          color: getColors(black70), borderRadius: BorderRadius.circular(5)),      padding: EdgeInsets.all(BaseUtils.getWidth(10)),      child: Text(        message,        style: TextStyle(color: getColors(white), fontSize: 16),      ),    ));  }  static showToastWidget(Widget widget, {Duration closeDuration}) {    OverlayEntry entry = OverlayEntry(builder: (context) => widget);    Overlay.of(context).insert(entry);    if (closeDuration != null) {      Future.delayed(closeDuration ?? Duration(seconds: 2)).then((value) {        entry.remove();      });    }  }  /// 自行定义弹窗及关闭  static alertWidget(Widget widget) {    OverlayEntry entry = OverlayEntry(builder: (context) => widget);    Overlay.of(context).insert(entry);    return entry;  }}