import 'package:flutter/material.dart';import 'package:flutter_waya/src/constant/WayColor.dart';import 'package:flutter_waya/waya.dart';class Loading extends Dialog {  final double value;  final Color backgroundColor;  final Animation<Color> valueColor;  final String semanticsLabel;  final String semanticsValue;  LoadingType loadingType;  TextStyle textStyle;  double strokeWidth;  String text;  Loading({    this.value,    this.textStyle,    this.backgroundColor,    this.valueColor,    this.strokeWidth,    this.semanticsLabel,    this.semanticsValue,    this.loadingType,    this.text,  }) {    if (textStyle == null) textStyle = TextStyle(color: getColors(black), fontSize: 14, fontWeight: FontWeight.w600);    if (text == null) text = '加载中...';    if (strokeWidth == null) strokeWidth = 4.0;    if (loadingType == null) loadingType = LoadingType.circular;  }  @override  Widget build(BuildContext context) {    return AlertBase(      onTap: () {},      child: Container(        padding: EdgeInsets.all(BaseUtils.getWidth(20)),        decoration: BoxDecoration(          color: backgroundColor ?? getColors(white),          borderRadius: BorderRadius.circular(8.0),        ),        child: Column(          mainAxisAlignment: MainAxisAlignment.spaceBetween,          crossAxisAlignment: CrossAxisAlignment.center,          mainAxisSize: MainAxisSize.min,          children: <Widget>[            Offstage(              offstage: loadingType != LoadingType.circular,              child: CircularProgressIndicator(                value: value,                backgroundColor: backgroundColor,                valueColor: valueColor,                strokeWidth: strokeWidth,                semanticsLabel: semanticsLabel,                semanticsValue: semanticsValue,              ),            ),            Offstage(              offstage: loadingType != LoadingType.linear,              child: LinearProgressIndicator(                value: value,                backgroundColor: backgroundColor,                valueColor: valueColor,                semanticsLabel: semanticsLabel,                semanticsValue: semanticsValue,              ),            ),            Offstage(              offstage: loadingType != LoadingType.refresh,              child: RefreshProgressIndicator(                value: value,                backgroundColor: backgroundColor,                valueColor: valueColor,                strokeWidth: strokeWidth,                semanticsLabel: semanticsLabel,                semanticsValue: semanticsValue,              ),            ),            Container(              height: BaseUtils.getHeight(20),              width: BaseUtils.getWidth(100),            ),            Text(              text,              style: textStyle,            )          ],        ),      ),    );  }}