import 'package:flutter/material.dart';import 'package:flutter_waya/flutter_waya.dart';import 'package:flutter_waya/src/model/MapModel.dart';import 'package:flutter_waya/src/widget/alert/AlertBase.dart';import 'package:flutter_waya/src/widget/alert/AlertTools.dart';import 'package:flutter_waya/src/widget/picker/DateTimePicker.dart';import 'package:flutter_waya/src/widget/picker/MultipleChoicePicker.dart';class PickerTools {  ///日期选择器  static void dateTimePicker({    ///主要用于关闭 软键盘    BuildContext context,    ///背景色    Color backgroundColor,    ///头部    Widget titleBottom,    Widget title,    ///头部右侧 确定    Widget sure,    ///头部左侧 取消    Widget cancel,    ///内容文字样式    TextStyle contentStyle,    ///选择框内单位文字样式    TextStyle unitStyle,    ///取消点击事件    GestureTapCallback cancelTap,    ///确认点击事件    ValueChanged<String> sureTap,    ///单个item的高度    double itemHeight,    ///单个item的宽度    double itemWidth,    ///整个弹框高度    double height,    ///开始时间    DateTime startDate,    ///默认选中时间    DateTime defaultDate,    ///结束时间    DateTime endDate,    ///补全双位数    bool dual,    ///是否显示单位    bool showUnit,    ///单位设置    DateTimePickerUnit unit,    /// 半径大小,越大则越平面,越小则间距越大    double diameterRatio,    /// 选中item偏移    double offAxisFraction,    ///表示车轮水平偏离中心的程度  范围[0,0.01]    double perspective,    ///放大倍率    double magnification,    ///是否启用放大镜    bool useMagnifier,    ///1或者2    double squeeze,    ScrollPhysics physics,    bool tapBackClose: false,  }) {    if (context != null) Tools.closeKeyboard(context);    Widget widget = AlertBase(        alignment: Alignment.bottomCenter,        onTap: !tapBackClose ? null : close,        child: DateTimePicker(            diameterRatio: diameterRatio,            offAxisFraction: offAxisFraction,            perspective: perspective,            magnification: magnification,            useMagnifier: useMagnifier,            squeeze: squeeze,            itemHeight: itemHeight,            itemWidth: itemWidth,            height: height,            startDate: startDate,            endDate: endDate,            defaultDate: defaultDate,            dual: dual,            showUnit: showUnit,            unit: unit,            backgroundColor: backgroundColor,            sure: sure,            title: title,            cancel: cancel,            titleBottom: titleBottom,            contentStyle: contentStyle,            unitStyle: unitStyle,//            pickerType: pickerType,            cancelTap: cancelTap ?? close,            sureTap: sureTap ?? close));    AlertTools.alertWidget(widget);  }  ///wheel 单列 取消确认 选择  static void multipleChoicePicker({    BuildContext context,    int initialIndex,    ///默认选中    Color backgroundColor,    ///背景色    String sureText,    ///确认按钮文字    String cancelText,    ///取消按钮文字    String titleText, //头部文字    TextStyle titleStyle, //头部文字样式    TextStyle sureStyle, //确认文字样式    TextStyle cancelStyle, //取消文字样式    GestureTapCallback cancelTap, //取消点击事件    ValueChanged<int> sureTap, //确认点击事件    double itemHeight, //单个item的高度    double itemWidth, //单个item的宽度    double height, //整个弹框高度    // 半径大小,越大则越平面,越小则间距越大    double diameterRatio,    // 选中item偏移    double offAxisFraction,    //表示车轮水平偏离中心的程度  范围[0,0.01]    double perspective,    //放大倍率    double magnification,    //是否启用放大镜    bool useMagnifier,    //是否启动高斯模糊    bool gaussian,    //1或者2    double squeeze,    ScrollPhysics physics,    @required int itemCount,    @required IndexedWidgetBuilder itemBuilder,  }) {    if (context != null) Tools.closeKeyboard(context);    Widget widget = AlertBase(        gaussian: gaussian,        alignment: Alignment.bottomCenter,        onTap: close,        child: MultipleChoicePicker(            itemCount: itemCount,            itemBuilder: itemBuilder,            diameterRatio: diameterRatio,            offAxisFraction: offAxisFraction,            perspective: perspective,            magnification: magnification,            useMagnifier: useMagnifier,            squeeze: squeeze,            itemHeight: itemHeight,            itemWidth: itemWidth,            height: height,            initialIndex: initialIndex,            sureText: sureText,            cancelText: cancelText,            titleText: titleText,            backgroundColor: backgroundColor,            titleStyle: titleStyle,            sureStyle: sureStyle,            cancelStyle: cancelStyle,            cancelTap: cancelTap ?? close,            sureTap: sureTap ?? close));    AlertTools.alertWidget(widget);  }  //关闭所有picker  static void close() {    AlertTools.close();  }}